mapper.select:
select sid,sFilingId,sPaperItems,sAccessory,sEnforcementMode,sCost,sUndertaker,sUndertakerDeptId,sAuditOpinion,sCreateTime,sCreateId,sCreateName,sStatus,url,auditorrole,punit from daily_supervisionFiling where sStatus!=-1

    and sStatus=#{sStatus}

    or sStatus=3

select sid,sFilingId,sPaperItems,sAccessory,sEnforcementMode,sCost,sUndertaker,sUndertakerDeptId,sAuditOpinion,sCreateTime,sCreateId,sCreateName,sStatus,url,auditorrole,punit from daily_supervisionFiling
where sStatus!=-1 and   sPaperItems like CONCAT('%',#{sPaperItems},'%')

    and sStatus=#{sStatus}

select sid,sFilingId,sPaperItems,sAccessory,sEnforcementMode,sCost,sUndertaker,sUndertakerDeptId,sAuditOpinion,sCreateTime,sCreateId,sCreateName,sStatus,url,auditorrole,punit from daily_supervisionFiling
where sStatus!=-1 and   sFilingId like CONCAT('%',#{sFilingId},'%')

    and sStatus=#{sStatus}
